INSERT INTO `herbal_recipe` VALUES (19, '秋季润肺汤', 'YINXU,PINGHE', 'AUTUMN', '汤品', 2, 60, 3, '[{\"name\": \"雪梨\", \"amount\": \"2个\"}, {\"name\": \"百合\", \"amount\": \"30g\"}, {\"name\": \"银耳\", \"amount\": \"1朵\"}, {\"name\": \"枸杞\", \"amount\": \"10g\"}, {\"name\": \"冰糖\", \"amount\": \"适量\"}]', '[\"雪梨去皮切块，银耳泡发\", \"百合洗净\", \"将银耳放入锅中煮30分钟\", \"加入雪梨、百合继续煮20分钟\", \"最后加枸杞和冰糖，煮5分钟即可\"]', '滋阴润肺，止咳化痰', '秋季干燥，咳嗽少痰，口干', '脾胃虚寒者少食', '{\"fat\": \"1g\", \"carbs\": \"36g\", \"protein\": \"3g\", \"calories\": \"150kcal\"}', '秋季气候干燥，此汤最适合润肺；雪梨不宜久煮', 'autumn_soup.jpg', '', 1420, 99, 1, 1, '2025-11-05 19:08:56', '2025-11-05 19:10:51');
INSERT INTO `herbal_recipe` VALUES (20, '冬季温补汤', 'YANGXU,QIXU', 'WINTER', '汤品', 2, 150, 4, '[{\"name\": \"乌鸡\", \"amount\": \"500g\"}, {\"name\": \"当归\", \"amount\": \"15g\"}, {\"name\": \"黄芪\", \"amount\": \"20g\"}, {\"name\": \"红枣\", \"amount\": \"10颗\"}, {\"name\": \"枸杞\", \"amount\": \"10g\"}, {\"name\": \"生姜\", \"amount\": \"3片\"}, {\"name\": \"盐\", \"amount\": \"适量\"}]', '[\"乌鸡洗净切块，焯水去血沫\", \"当归、黄芪洗净\", \"将所有材料放入炖盅\", \"加适量清水，大火烧开\", \"转小火炖2.5小时\", \"加盐调味即可\"]', '温阳补气，养血调经', '冬季进补，气血两虚，手足冰冷', '感冒发热期间不宜', '{\"fat\": \"14g\", \"carbs\": \"10g\", \"protein\": \"32g\", \"calories\": \"300kcal\"}', '冬季是进补的最佳时节；乌鸡营养丰富，适合体虚者', 'winter_soup.jpg', '', 1580, 115, 1, 1, '2025-11-05 19:08:56', '2025-11-05 19:08:56');
